import { useEffect, useState } from "react"
import { apilist } from "../services/apilist";
import axios from 'axios';
import { Link } from "react-router-dom";



export default function Dashboarrd(){

    const [links,setLinks] = useState([]);
    const [filteredLinks,setFilteredLinks]=useState([]);
    const [url,setUrl]=useState("");
    const [code,setCode]=useState("");
    const [search,setSearch]=useState("");
    const [loading,setLoading]=useState(false);
    const [error, setError] = useState(null);


    const fetchLinks = async() =>{
        try{
            const res = await axios.get(apilist.get_links);
            setLinks(res.data);
            setFilteredLinks(res.data);
            setError(null);
        }
        catch(err)
        {
            console.error("FetchLinks",err);
            setError("Unable to fetch links");
        }
    }

    useEffect(()=> {
        fetchLinks();
    },[])

      useEffect(() => {
    if (!search) {
      setFilteredLinks(links);
      return;
    }
    const lower = search.toLowerCase();
    setFilteredLinks(
      links.filter(
        (l) =>
          (l.code && l.code.toLowerCase().includes(lower)) ||
          (l.url && l.url.toLowerCase().includes(lower))
      )
    );
  }, [search, links]);

    const addLink = async ()=>{
        if(!url) return alert ("Please enter a URL");
        setLoading(true);
        try{
            await axios.post(apilist.post_links,{url,code:code || null});
            setUrl("");
            setCode("");
            setSearch("");
            await fetchLinks();
        }
        catch(err)
        {
            alert(err.response?.data?.error || "Failed to add link");
        }
        finally{
            setLoading(false);
        }
    }

    return(
        <div className="app-container">
            <h1 className="page-title">TinyLink Dashboard</h1>
            <div className="controls">
                <input
                className="input flex"
                placeholder="Enter URL"
                value={url}
                onChange={(e)=>setUrl(e.target.value)}
                />
                <input
                className="input"
                placeholder="Custom code (optional)"
                value={code}
                onChange={(e)=>setCode(e.target.value)}
                />
                <button className="btn btn-primary" onClick={addLink} disabled={loading}>
                    {loading ? "Adding...":"Add"}
                </button>
                 </div>

      <div className="search-wrapper">
        <input
          className="input"
          style={{ width: "100%", maxWidth: "320px" }}
          placeholder="Search by code or URL"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
      </div>

      <div className="table-wrap">
        <table className="table">
          <thead>
            <tr>
              <th>Code</th>
              <th>URL</th>
              <th>Clicks</th>
              <th>Last Clicked</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredLinks.map((l) => (
              <tr key={l.code}>
                <td style={{verticalAlign:"middle"}}>
                  <div className="code-cell">
                    <a className="code-link" href={`/${l.code}`} target="_blank" rel="noreferrer">
                      {l.code}
                    </a>
                    <button className="copy-btn" onClick={() => copyToClipboard(l.code)}>Copy</button>
                  </div>
                </td>
                <td style={{ maxWidth: 420 }} title={l.url} className="small">{l.url}</td>
                <td>{l.clicks}</td>
                <td>{l.last_clicked || "-"}</td>
                <td>
                  <div className="actions">
                    <button className="btn btn-danger" onClick={() => deleteLink(l.code)}>Delete</button>
                    <Link className="btn btn-ghost" to={`/code/${l.code}`}>Stats</Link>
                  </div>
                </td>
              </tr>
            ))}

            {filteredLinks.length === 0 && (
              <tr>
                <td colSpan="5">
                  <div className="empty">No matching links</div>
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}
