import { useEffect, useState } from "react"
import { apilist } from "../services/apilist";
import axios from 'axios';
import { Link } from "react-router-dom";



export default function Dashboarrd() {

    const [links, setLinks] = useState([]);
    const [filteredLinks, setFilteredLinks] = useState([]);
    const [url, setUrl] = useState("");
    const [code, setCode] = useState("");
    const [urlError, setUrlError] = useState("");
    const [codeError, setCodeError] = useState("");
    const [globalError, setGlobalError] = useState("");
    const [search, setSearch] = useState("");
    const [loading, setLoading] = useState(false);
    const [adding, setAdding] = useState(false);
    const [successMsg, setSuccessMsg] = useState("");


      const validateUrl = (value) => {
    try {
      const u = new URL(value);
      if (u.protocol === "http:" || u.protocol === "https:") return "";
    } catch {}
    return "Enter a valid URL (http or https)";
  };


   const validateCode = (value) => {
    if (!value) return ""; // optional
    const re = /^[A-Za-z0-9]{6,8}$/;
    return re.test(value)
      ? ""
      : "Code must be 6–8 letters/numbers only.";
  };


    const fetchLinks = async () => {

         setLoading(true);
    setGlobalError("");
        try {
            const res = await axios.get(apilist.get_links);
            setLinks(res.data);
            setFilteredLinks(res.data);
        }
        catch (err) {
      setGlobalError("⚠ Unable to fetch links. Check backend.");
        }
            setLoading(false);

    }

    useEffect(() => {
        fetchLinks();
    }, [])

    useEffect(() => {
        if (!search) {
            setFilteredLinks(links);
            return;
        }
        const lower = search.toLowerCase();
        setFilteredLinks(
            links.filter(
                (l) =>
                    (l.code && l.code.toLowerCase().includes(lower)) ||
                    (l.url && l.url.toLowerCase().includes(lower))
            )
        );
    }, [search, links]);

    const addLink = async () => {
    const urlErr = validateUrl(url);
    const codeErr = validateCode(code);

    setUrlError(urlErr);
    setCodeError(codeErr);

    if (urlErr || codeErr) return;

    setAdding(true);
    setSuccessMsg("");

    try {
      await axios.post(apilist.post_links, { url, code: code || null });

      // SUCCESS
      setSuccessMsg("Link added successfully ✔");
      setUrl("");
      setCode("");
      setSearch("");

      fetchLinks();
    } catch (err) {
      const msg = err.response?.data?.error || "Failed to add link";
      setGlobalError("⚠ " + msg);
    }

    setAdding(false);
  };

    const deleteLink = async (c) => {
        if (!window.confirm("Delete this link?")) return;
        try {
            await axios.delete(apilist.delete_link(c));
            await fetchLinks();
        } catch (err) {
            alert(err.response?.data?.error || "Failed to delete");
        }
    };

    const copyToClipboard = (c) => {
        const short = apilist.redirect_link(c);
        navigator.clipboard.writeText(short).then(() => {
            alert("Short URL copied to clipboard");
        }).catch(() => alert("Copy failed"));
    };


    return (
        <div className="app-container">
            <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
                <h1 className="page-title">TinyLink Dashboard</h1>
                <Link to="/healthz" className="btn btn-primary">Health Check</Link>
            </div>

            <div className="controls">
                <input
                    className="input flex"
                    placeholder="Enter URL"
                    value={url}
                    onChange={(e) => setUrl(e.target.value)}
                />
                <input
                    className="input"
                    placeholder="Custom code (optional)"
                    value={code}
                    onChange={(e) => setCode(e.target.value)}
                />
                <button className="btn btn-primary" onClick={addLink} disabled={loading}>
                    {loading ? "Adding..." : "Add"}
                </button>
            </div>

            <div className="search-wrapper">
                <input
                    className="input"
                    style={{ width: "100%", maxWidth: "320px" }}
                    placeholder="Search by code or URL"
                    value={search}
                    onChange={(e) => setSearch(e.target.value)}
                />
            </div>

            <div className="table-wrap">
                <table className="table">
                    <thead>
                        <tr>
                            <th>Code</th>
                            <th>URL</th>
                            <th>Clicks</th>
                            <th>Last Clicked</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {filteredLinks.map((l) => (
                            <tr key={l.code}>
                                <td style={{ verticalAlign: "middle" }}>
                                    <div className="code-cell">
                                        <a className="code-link" href={apilist.redirect_link(l.code)} target="_blank" rel="noreferrer" style={{ pointerEvents: "auto", position: "relative", zIndex: 10 }}>
                                            {l.code}
                                        </a>
                                        <button className="copy-btn" onClick={() => copyToClipboard(l.code)}>Copy</button>
                                    </div>
                                </td>
                                <td style={{ maxWidth: 420 }} title={l.url} className="small">{l.url}</td>
                                <td>{l.clicks}</td>
                                <td>
                                    {l.last_clicked
                                        ? new Date(l.last_clicked).toLocaleString("en-IN", {
                                            timeZone: "Asia/Kolkata",
                                        })
                                        : "-"}
                                </td>
                                <td>
                                    <div className="actions">
                                        <button className="btn btn-danger" onClick={() => deleteLink(l.code)}>Delete</button>
                                        <Link className="btn btn-ghost" to={`/code/${l.code}`}>Stats</Link>
                                    </div>
                                </td>
                            </tr>
                        ))}

                        {filteredLinks.length === 0 && (
                            <tr>
                                <td colSpan="5">
                                    <div className="empty">No matching links</div>
                                </td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        </div>
    );
}
