import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import { apilist } from "../services/apilist";


export default function Stats() {
  const { code } = useParams();
  const [link, setLink] = useState(null);

 useEffect(() => {
  const fetchStats = async () => {
    try {
      const res = await axios.get(apilist.link_details(code));
      setLink(res.data);
    } catch (err) {
      alert(err.response?.data?.error || "Failed to fetch stats");
    }
  };

  fetchStats();
}, [code]);


  if (!link) return <div className="app-container"><div className="empty">Loading...</div></div>;

  return (
    <div className="app-container">
      <h1 className="page-title">Stats for {link.code}</h1>

      <div className="card" style={{ maxWidth: 760 }}>
        <div className="stats-row"><strong>URL:</strong> <a href={link.url} target="_blank" rel="noreferrer" className="code-link">{link.url}</a></div>
        <div className="stats-row"><strong>Clicks:</strong> {link.clicks}</div>
        <div className="stats-row"><strong>Last Clicked:</strong> {l.last_clicked
                                        ? new Date(l.last_clicked).toLocaleString("en-IN", {
                                            timeZone: "Asia/Kolkata",
                                        })
                                        : "-"}</div>
        <div className="stats-row"><strong>Created At:</strong> {link.created_at}</div>
      </div>

      <div style={{ marginTop: 12 }}>
        <Link to="/" className="btn btn-primary">Back to Dashboard</Link>
      </div>
    </div>
  );
}
