import { useEffect, useState } from "react";
import { apilist } from "../services/apilist";
import axios from "axios";
import { Link } from "react-router-dom";

export default function Dashboarrd() {
  const [links, setLinks] = useState([]);
  const [filteredLinks, setFilteredLinks] = useState([]);

  // form inputs
  const [url, setUrl] = useState("");
  const [code, setCode] = useState("");

  // validation errors
  const [urlError, setUrlError] = useState("");
  const [codeError, setCodeError] = useState("");
  const [globalError, setGlobalError] = useState("");

  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(false);
  const [adding, setAdding] = useState(false);
  const [successMsg, setSuccessMsg] = useState("");

  // ----------------------------
  // VALIDATION
  // ----------------------------

  const validateUrl = (value) => {
    try {
      const u = new URL(value);
      if (u.protocol === "http:" || u.protocol === "https:") return "";
    } catch {}
    return "Enter a valid URL (http or https)";
  };

  const validateCode = (value) => {
    if (!value) return ""; // optional
    const re = /^[A-Za-z0-9]{6,8}$/;
    return re.test(value)
      ? ""
      : "Code must be 6–8 letters/numbers only.";
  };

  // ----------------------------
  // FETCH LINKS
  // ----------------------------

  const fetchLinks = async () => {
    setLoading(true);
    setGlobalError("");
    try {
      const res = await axios.get(apilist.get_links);
      setLinks(res.data);
      setFilteredLinks(res.data);
    } catch (err) {
      setGlobalError("⚠ Unable to fetch links. Check backend.");
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchLinks();
  }, []);

  // ----------------------------
  // SEARCH FILTER
  // ----------------------------
  useEffect(() => {
    if (!search) return setFilteredLinks(links);
    const lower = search.toLowerCase();
    setFilteredLinks(
      links.filter(
        (l) =>
          l.code?.toLowerCase().includes(lower) ||
          l.url?.toLowerCase().includes(lower)
      )
    );
  }, [search, links]);

  // ----------------------------
  // ADD LINK
  // ----------------------------
  const addLink = async () => {
    const urlErr = validateUrl(url);
    const codeErr = validateCode(code);

    setUrlError(urlErr);
    setCodeError(codeErr);

    if (urlErr || codeErr) return;

    setAdding(true);
    setSuccessMsg("");

    try {
      await axios.post(apilist.post_links, { url, code: code || null });

      // SUCCESS
      setSuccessMsg("Link added successfully ✔");
      setUrl("");
      setCode("");
      setSearch("");

      fetchLinks();
    } catch (err) {
      const msg = err.response?.data?.error || "Failed to add link";
      setGlobalError("⚠ " + msg);
    }

    setAdding(false);
  };

  // ----------------------------
  // DELETE
  // ----------------------------
  const deleteLink = async (c) => {
    if (!window.confirm("Delete this link?")) return;
    try {
      await axios.delete(apilist.delete_link(c));
      fetchLinks();
    } catch (err) {
      setGlobalError("Failed to delete link");
    }
  };

  const copyToClipboard = (c) => {
    navigator.clipboard
      .writeText(apilist.redirect_link(c))
      .then(() => setSuccessMsg("Copied to clipboard ✔"))
      .catch(() => setGlobalError("Copy failed"));
  };

  // ----------------------------
  // RENDER
  // ----------------------------
  return (
    <div className="app-container">
      <h1 className="page-title">TinyLink Dashboard</h1>

      {/* GLOBAL ERROR */}
      {globalError && (
        <div style={{ color: "red", marginBottom: 10 }}>{globalError}</div>
      )}

      {/* SUCCESS MESSAGE */}
      {successMsg && (
        <div style={{ color: "green", marginBottom: 10 }}>{successMsg}</div>
      )}

      {/* FORM */}
      <div className="controls">
        <div style={{ width: "100%" }}>
          <input
            className="input flex"
            placeholder="Enter URL"
            value={url}
            onFocus={() => setUrlError("")}
            onChange={(e) => setUrl(e.target.value)}
          />
          {urlError && <div style={{ color: "red", marginTop: 4 }}>{urlError}</div>}
        </div>

        <div>
          <input
            className="input"
            placeholder="Custom code (optional)"
            value={code}
            onFocus={() => setCodeError("")}
            onChange={(e) => setCode(e.target.value)}
          />
          {codeError && <div style={{ color: "red", marginTop: 4 }}>{codeError}</div>}
        </div>

        <button
          className="btn btn-primary"
          disabled={adding || validateUrl(url) !== ""}
          onClick={addLink}
        >
          {adding ? "Adding..." : "Add"}
        </button>
      </div>

      {/* SEARCH */}
      <div className="search-wrapper">
        <input
          className="input"
          style={{ width: "100%", maxWidth: "320px" }}
          placeholder="Search by code or URL"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
      </div>

      {/* LOADING */}
      {loading && <div className="empty">Loading...</div>}

      {/* TABLE */}
      {!loading && (
        <div className="table-wrap">
          <table className="table">
            <thead>
              <tr>
                <th>Code</th>
                <th>URL</th>
                <th>Clicks</th>
                <th>Last Clicked</th>
                <th>Actions</th>
              </tr>
            </thead>

            <tbody>
              {filteredLinks.map((l) => (
                <tr key={l.code}>
                  <td>
                    <div className="code-cell">
                      <a
                        className="code-link"
                        href={apilist.redirect_link(l.code)}
                        target="_blank"
                        rel="noreferrer"
                      >
                        {l.code}
                      </a>
                      <button className="copy-btn" onClick={() => copyToClipboard(l.code)}>
                        Copy
                      </button>
                    </div>
                  </td>

                  <td className="small" style={{ maxWidth: 420 }}>
                    {l.url}
                  </td>

                  <td>{l.clicks}</td>

                  <td>
                    {l.last_clicked
                      ? new Date(l.last_clicked).toLocaleString("en-IN", {
                          timeZone: "Asia/Kolkata",
                        })
                      : "-"}
                  </td>

                  <td>
                    <div className="actions">
                      <button className="btn btn-danger" onClick={() => deleteLink(l.code)}>
                        Delete
                      </button>
                      <Link className="btn btn-ghost" to={`/code/${l.code}`}>
                        Stats
                      </Link>
                      <Link className="btn btn-ghost" to="/healthz">Health</Link>
                    </div>
                  </td>
                </tr>
              ))}

              {filteredLinks.length === 0 && (
                <tr>
                  <td colSpan="5">
                    <div className="empty">No matching links</div>
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
